import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { CategoryModel } from 'src/app/models/category-model';
import { BookModel } from 'src/app/models/book-model';
import { DataService } from 'src/app/services/data.service';

@Component({
    selector: 'app-list',
    standalone: true,
    imports: [CommonModule],
    templateUrl: './list.component.html',
    styleUrls: ['./list.component.css']
})
export class ListComponent implements OnInit {

    public categories: CategoryModel[];
    public books: BookModel[];

    public constructor(private dataService: DataService) { }

    public async ngOnInit() {
        try {
            this.categories = await this.dataService.getAllCategories();

        }
        catch (err: any) {
            alert(err.message);
        }
    }

    public async getBooks(args: Event) {
        try {
            this.books = await this.dataService.getAllBooks();
        }
        catch (err: any) {
            alert(err.message);
        }
    }

    public async deleteMe(_id: string) {
        try {
            const sure = window.confirm("Are you sure?");
            if (!sure) return;
            await this.dataService.deleteBook(_id);
            alert("Book deleted");
            this.books = this.books.filter(b => b._id !== _id)
        }
        catch (err: any) {
            alert(err.message);
        }
    }
}
<div>

    <select (change)="getBooks($event)">
        <option disabled selected>Select Category</option>
        <option *ngFor="let c of categories" [value]="c._id">{{c.categoryName}}</option>
    </select>

    <table>
        <thead>
            <tr>
                <th>Name</th>
                <th>Category</th>
                <th>Description</th>
                <th>Address</th>
                <th>Latitude</th>
                <th>Longitude</th>
                <th>Delete</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let s of shops">
                <td>{{s.name}}</td>
                <td>{{s.category.categoryName}}</td>
                <td>{{s.description}}</td>
                <td>{{s.address}}</td>
                <td>{{s.latitude}}</td>
                <td>{{s.longitude}}</td>
                <td>
                    <button (click)="deleteMe(s._id)">❌</button>
                </td>
            </tr>
        </tbody>
    </table>


</div>
