import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { CategoryModel } from 'src/app/models/category.model';
import { DataService } from 'src/app/services/data.service';
import { BookModel } from 'src/app/models/book.model';

@Component({
  selector: 'app-insert-book',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './insert-book.component.html',
  styleUrls: ['./insert-book.component.css'],
})
export class InsertBookComponent implements OnInit {
  public categories: CategoryModel[];
  public book = new BookModel();

  constructor(private dataService: DataService, private router: Router) {}

  ngOnInit(): void {
    this.loadCategories();
  }

  async loadCategories() {
    try {
      this.categories = await this.dataService.getAllCategories();
    } catch (err: any) {
      alert(err.message);
    }
  }

  async send() {
    try {
      await this.dataService.addBook(this.book);
      alert('Book added!');
      this.router.navigateByUrl('/list');
    } catch (err: any) {
      alert(err.message);
    }
  }
}
